-- Enable UUID extension
create extension if not exists "uuid-ossp";

-- Create Universities table if it doesn't exist
create table if not exists public.universities (
  id uuid default uuid_generate_v4() primary key,
  slug text unique not null,
  name text not null,
  country text not null,
  city text not null,
  cost_description text,
  ielts_score numeric,
  flag_emoji text,
  scholarship_info text,
  details_json jsonb default '{}'::jsonb,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Enable RLS
alter table public.universities enable row level security;

-- Create policy if it doesn't exist (using DO block to avoid error if exists)
do $$
begin
  if not exists (
    select from pg_policies where tablename = 'universities' and policyname = 'Universities are viewable by everyone'
  ) then
    create policy "Universities are viewable by everyone" on public.universities for select using (true);
  end if;
end
$$;

-- Insert 50 Universities
ON CONFLICT (slug) DO UPDATE SET
    name = EXCLUDED.name,
    country = EXCLUDED.country,
    city = EXCLUDED.city,
    cost_description = EXCLUDED.cost_description,
    ielts_score = EXCLUDED.ielts_score,
    flag_emoji = EXCLUDED.flag_emoji,
    details_json = EXCLUDED.details_json;
